                    {"name": f"{sell_token_symbol} Sold", "type": "integer", "friendly_name": f"{sell_token_symbol} Sold"},
                    {"name": f"{buy_token_symbol} Received", "type": "integer", "friendly_name": f"{buy_token_symbol} Received"},
